sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}else{
		if(newValue != ''){			
			if (newValue == 'No'){
				g_form.setMandatory('u_wex_primary_comments',true);
			}else{
				g_form.setMandatory('u_wex_primary_comments',false);
			}
			var ajax = new GlideAjax('global.ClientDateTimeUtils');
			ajax.addParam('sysparm_name','getNowTime');
			ajax.getXML(getTime);
		}else{
			g_form.clearValue('u_wex_primary_time');
			g_form.setMandatory('u_wex_primary_comments',false);
		}
	}
}

function getTime(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('u_wex_primary_time',answer);
}
